<!-- Application -->
<template>
    <header class="demo-header">
        <div id="title-container">
            <a
                id="title"
                :href="link"
            >
                <h1>{{ title }}</h1>
            </a>
        </div>
        <slot />
        <div id="tools">
            <bryntum-button
                text="Download Trial"
                href="https://bryntum.com/download/?product=grid"
                cls="b-green b-raised"
            />
            <bryntum-fullscreen-button />
        </div>
    </header>
</template>

<script>

import BryntumFullscreenButton from './BryntumFullscreenButton.vue';
import BryntumButton from './BryntumButton.vue';

export default {

    name : 'BryntumDemoHeader',

    components : {
        BryntumFullscreenButton,
        BryntumButton
    },

    computed : {
        link() {
            const match    = /(.*?\/)(frameworks\/.*?)\/dist/.exec(document.location.href);
            return match ? `${match[1]}#example-${match[2].replace(/\//gm, '-')}` : '#';
        }
    },

    props : {
        title : { type : String, default : document.title.split(' - ')[1] || document.title }
    }

};

</script>
